    double num1 = 0, num2 = 0, resultado = 0;
String operador = "";
boolean esperandoNuevoNumero = false;

private void agregarNumero(String numero) {
    if (esperandoNuevoNumero) {
        txtResultado.setText(numero);
        esperandoNuevoNumero = false;
    } else {
        String textoActual = txtResultado.getText();
        if (textoActual.equals("0")) {
            txtResultado.setText(numero);
        } else {
            txtResultado.setText(textoActual + numero);
        }
    }
}
