    private void BtnEqualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnEqualActionPerformed
           // Si no se seleccionó operador, no hacemos nada
    if (operador.isEmpty()) {
        return;
    }

    num2 = Double.parseDouble(txtResultado.getText());

    switch (operador) {
        case "+":
            resultado = num1 + num2;
            break;
        case "-":
            resultado = num1 - num2;
            break;
        case "*":
            resultado = num1 * num2;
            break;
        case "/":
            if (num2 != 0) {
                resultado = num1 / num2;
            } else {
                txtResultado.setText("Error");
                return;
            }
            break;
    }

    txtResultado.setText(String.format(java.util.Locale.US, "%s", resultado).replaceAll("\\.?0*$", ""));

    esperandoNuevoNumero = true;
    operador = ""; // Limpia el operador para evitar repetir la operación
    }//GEN-LAST:event_BtnEqualActionPerformed

